mapper.update:
update product
    set is_deleted = 1
    where id = #{id}

mapper.select:
select p.*, b.name brandName , c1.name category1Name , c2.name category2Name , c3.name category3Name from product p left join brand b on p.brand_id = b.id left join category c1 on p.category1_id = c1.id left join category c2 on p.category2_id =c2.id left join category c3 on p.category3_id =c3.id
and brand_Id = #{brandId}
and category1_Id = #{category1Id}
and category2_Id = #{category2Id}
and category3_Id = #{category3Id}
and b.is_deleted = 0 and c1.is_deleted = 0 and c2.is_deleted = 0 and c3.is_deleted = 0
